// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

interface proposalEvents{
    event VoteCasted(address voter, uint256 ProposalID, uint256[] _tokens);

    event ProposalCreated(
        uint proposalId,
        address creator,
        string description,
        string[] _options,
        address[] voters,
        uint256 votingTimeInHours
    );
}
